mapper.select:
SELECT

FROM tab_user u JOIN tab_car c ON u.user_id = c.user_id AND user_type = 1
WHERE car_id = #{carId}

SELECT c.car_id, plate_no, spec, c.user_id, NAME, gender, avatar, age FROM tab_user u JOIN tab_car c ON u.user_id = c.user_id AND user_type = 1 AND car_id IN ( SELECT tab_task.car_id FROM tab_task WHERE #{currentTime} BETWEEN start_time AND end_time AND `status` = 1
AND tab_task.start_spot = #{startSpot}
AND tab_task.end_spot = #{endSpot}
)

mapper.update:
UPDATE tab_car
SET user_id = #{userId}
WHERE car_id = #{carId}
